    margin: 4rem auto 0;
    width: 70%;
    text-align: center;
}

.certificate-footer-item {
    color: #192734;
}

.cursive-font {
    font-family: cursive;
    font-weight: 600;
}

.certificate-divider {
    margin: 0.5rem 0;
}

.certificate-expiry {
    margin-left: 2rem;
