}
 .even {
     background: #fbf8f0;
}
 .odd {
     background: #fefcf9;
}

.item-list {
	display: grid;
	grid-template-columns: 2.5fr 100px 2fr 1.5fr 2fr 2fr 1fr 1fr;
	grid-column-gap: 2px;
	padding: 2px 0px 2px 0px;
}

.solid {
	border-top: 3px solid black;
	width: 100%;
	margin: 0;
}

.dashed {
	border-top: 1px dashed black !important;
	width: 100%;
	margin: 0;
}

.center {
	width: 100%;
	text-align: center;
	margin: 0px;
}

.head-row {
	margin: 0px;
	display: flex;
	column-gap: 24px;
	flex: 1;
}

.row-item {
	flex: 1;
}

