def submit(doctype, doc, field):
    company = frappe.get_doc('Company', doc.company)
    if len(doc.items) > 0:
        for item in doc.items:
            item.delete()
    doc.items = []
    expense_item = frappe.get_doc('Item', company.tms_expense_item) 
    ref_doc = frappe.get_doc(doctype, field) 
    doc.append("items", {
        'item_code': expense_item.name,
        'item_name': expense_item.item_name,
        'rate': ref_doc.payment_total,
        'base_rate': ref_doc.payment_total,
        'qty': 1,
        'ps': '-',
        'uom': expense_item.stock_uom,

    })

